                      <TeamsContainer>
                        <TeamInfo>
                          <TeamLogo>{match.homeTeam.name.substring(0, 1)}</TeamLogo>
                          <TeamName>{match.homeTeam.name}</TeamName>
                          {match.homeTeam.rating && (
                            <TeamRating>Rating: {match.homeTeam.rating.toFixed(1)}</TeamRating>
                          )}
                        </TeamInfo>
                        
                        <ScoreContainer>
                          <ScoreDisplay>vs</ScoreDisplay>
                          <ScoreLabel>MATCH</ScoreLabel>
                        </ScoreContainer>
                        
                        <TeamInfo>
                          <TeamLogo>{match.awayTeam.name.substring(0, 1)}</TeamLogo>
                          <TeamName>{match.awayTeam.name}</TeamName>
                          {match.awayTeam.rating && (
                            <TeamRating>Rating: {match.awayTeam.rating.toFixed(1)}</TeamRating>
                          )}
                        </TeamInfo>
                      </TeamsContainer>
                      
                      <OddsContainer>
                        <OddButton isRecommended={match.recommended === 'home'}>
                          <OddType>Home (1)</OddType>
                          <OddValue isRecommended={match.recommended === 'home'}>{match.odds.home.toFixed(2)}</OddValue>
                          <OddProbability>{match.probability.home}</OddProbability>
                        </OddButton>
                        
                        <OddButton isRecommended={match.recommended === 'draw'}>
                          <OddType>Draw (X)</OddType>
                          <OddValue isRecommended={match.recommended === 'draw'}>{match.odds.draw.toFixed(2)}</OddValue>
                          <OddProbability>{match.probability.draw}</OddProbability>
                        </OddButton>
                        
                        <OddButton isRecommended={match.recommended === 'away'}>
                          <OddType>Away (2)</OddType>
                          <OddValue isRecommended={match.recommended === 'away'}>{match.odds.away.toFixed(2)}</OddValue>
                          <OddProbability>{match.probability.away}</OddProbability>
                        </OddButton>
                      </OddsContainer>
                      
                      <VenueInfo>
                        <StadiumIcon /> {match.venue}
                      </VenueInfo>
                    </MatchContent>
                  </MatchCard>
                ))}
              </MatchesGrid>
            ) : (
              <NoDataMessage>
                <NoDataIcon />
                <div>No upcoming matches in the next 24 hours.</div>
              </NoDataMessage>
            )}
          </MatchesContainer>
        </div>
      </DashboardContainer>
    </DashboardLayout>
  );
};

export default Dashboard;
