import React, { useState } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import DashboardLayout from '../components/layout/DashboardLayout';
import KPIRow from '../components/dashboard/KPIRow';
import {
  LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';

// Styled Components
const DashboardContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  gap: 1.5rem;
`;

const WelcomeSection = styled.div`
  margin-top: 1rem;
`;

const Title = styled.h1`
  font-size: 2rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
`;

const Subtitle = styled.p`
  color: #B4B4C6;
  font-size: 1rem;
`;

const SectionTitle = styled.h2`
  font-size: 1.25rem;
  font-weight: 600;
  margin-bottom: 1rem;
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const ViewAllButton = styled(Link)`
  font-size: 0.875rem;
  color: #6366F1;
  text-decoration: none;
  display: flex;
  align-items: center;
  
  &:hover {
    text-decoration: underline;
  }
  
  svg {
    margin-left: 0.25rem;
  }
`;

const StatsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 1.5rem;
  margin-top: 1.5rem;
  
  @media (max-width: 992px) {
    grid-template-columns: 1fr;
  }
`;

const StatsCard = styled.div`
  background-color: #252535;
  border-radius: 10px;
  padding: 1.5rem;
`;

const StatsCardTitle = styled.h3`
  font-size: 1rem;
  font-weight: 500;
  color: #B4B4C6;
  margin-bottom: 1.5rem;
`;

const MatchesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 1rem;
  margin-top: 0.5rem;
`;

const MatchCard = styled.div`
  background-color: #252535;
  border-radius: 10px;
  overflow: hidden;
  position: relative;
  transition: transform 0.2s ease;
  
  &:hover {
    transform: translateY(-3px);
  }
`;

const LeagueHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0.75rem 1rem;
  background-color: rgba(255,255,255,0.05);
`;

const LeagueName = styled.div`
  font-size: 0.75rem;
  color: #B4B4C6;
`;

const MatchTime = styled.div`
  font-size: 0.75rem;
  color: ${props => props.live ? '#EF4444' : '#B4B4C6'};
  display: flex;
  align-items: center;
  
  svg {
    margin-right: 0.25rem;
  }
`;

const MatchContent = styled.div`
  padding: 1rem;
`;

const TeamsContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 0.75rem;
`;

const TeamInfo = styled.div`
  display: flex;
  align-items: center;
`;

const TeamLogo = styled.div`
  width: 32px;
  height: 32px;
  border-radius: 50%;
  background-color: #2A2A3C;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 600;
  margin-right: 0.5rem;
`;

const TeamName = styled.div`
  font-weight: 500;
`;

const Score = styled.div`
  font-weight: 600;
  font-size: 1.25rem;
  padding: 0 0.75rem;
`;

const OddsContainer = styled.div`
  display: flex;
  justify-content: space-between;
  gap: 0.5rem;
  margin-top: 0.75rem;
`;

const OddButton = styled.div<{isHighlighted?: boolean}>`
  flex: 1;
  background-color: ${props => props.isHighlighted ? 'rgba(99, 102, 241, 0.2)' : 'rgba(255,255,255,0.05)'};
  color: ${props => props.isHighlighted ? '#6366F1' : 'white'};
  border: ${props => props.isHighlighted ? '1px solid #6366F1' : '1px solid transparent'};
  border-radius: 6px;
  padding: 0.5rem;
  text-align: center;
  font-size: 0.875rem;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover {
    background-color: ${props => props.isHighlighted ? 'rgba(99, 102, 241, 0.3)' : 'rgba(255,255,255,0.1)'};
  }
`;

const LiveBadge = styled.div`
  position: absolute;
  top: 1.5rem;
  right: -1.75rem;
  background-color: #EF4444;
  color: white;
  font-size: 0.7rem;
  font-weight: 600;
  padding: 0.15rem 1.5rem;
  transform: rotate(45deg);
`;

const EmptyState = styled.div`
  background-color: #252535;
  border-radius: 10px;
  padding: 3rem 1rem;
  text-align: center;
  color: #B4B4C6;
`;

// Icons
const ArrowRightIcon = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path d="M5 12H19M19 12L12 5M19 12L12 19" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
  </svg>
);

const LiveIcon = () => (
  <svg width="12" height="12" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path d="M5.63605 18.364C9.15076 21.8787 14.8492 21.8787 18.364 18.364C21.8787 14.8492 21.8787 9.15076 18.364 5.63605C14.8492 2.12134 9.15076 2.12134 5.63605 5.63605C2.12134 9.15076 2.12134 14.8492 5.63605 18.364Z" stroke="#EF4444" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    <path d="M12 8V12L15 15" stroke="#EF4444" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
  </svg>
);

// Mock Data for Charts
const last7DaysROI = [
  { day: 'Sat', roi: 2.5 },
  { day: 'Sun', roi: -1.2 },
  { day: 'Mon', roi: 3.7 },
  { day: 'Tue', roi: 1.8 },
  { day: 'Wed', roi: -0.5 },
  { day: 'Thu', roi: 4.2 },
  { day: 'Fri', roi: 2.9 }
];

const last7DaysBets = [
  { day: 'Sat', wins: 3, losses: 2 },
  { day: 'Sun', wins: 2, losses: 3 },
  { day: 'Mon', wins: 4, losses: 1 },
  { day: 'Tue', wins: 3, losses: 2 },
  { day: 'Wed', wins: 2, losses: 2 },
  { day: 'Thu', wins: 5, losses: 0 },
  { day: 'Fri', wins: 3, losses: 1 }
];

const Dashboard: React.FC = () => {
  const [liveMatches] = useState([
    {
      id: 1,
      league: "Premier League",
      datetime: "LIVE 65'",
      isLive: true,
      homeTeam: "Chelsea",
      awayTeam: "Liverpool",
      homeScore: 2,
      awayScore: 1,
      homeOdd: 2.24,
      drawOdd: 3.74,
      awayOdd: 1.69,
      recommended: 'home'
    },
    {
      id: 2,
      league: "La Liga",
      datetime: "LIVE 32'",
      isLive: true,
      homeTeam: "Barcelona",
      awayTeam: "Real Madrid",
      homeScore: 0,
      awayScore: 1,
      homeOdd: 1.95,
      drawOdd: 3.40,
      awayOdd: 2.85,
      recommended: 'away'
    }
  ]);
  
  const [upcomingMatches] = useState([
    {
      id: 1,
      league: "Premier League",
      datetime: "Today, 17:30",
      isLive: false,
      homeTeam: "Brentford",
      awayTeam: "Man Utd",
      homeOdd: 2.69,
      drawOdd: 2.69,
      awayOdd: 2.14,
      recommended: 'draw'
    },
    {
      id: 2,
      league: "Serie A",
      datetime: "Today, 20:45",
      isLive: false,
      homeTeam: "Inter",
      awayTeam: "Milan",
      homeOdd: 1.80,
      drawOdd: 3.11,
      awayOdd: 2.74,
      recommended: 'home'
    },
    {
      id: 3,
      league: "Bundesliga",
      datetime: "Tomorrow, 15:30",
      isLive: false,
      homeTeam: "Bayern",
      awayTeam: "Dortmund",
      homeOdd: 1.65,
      drawOdd: 3.50,
      awayOdd: 3.75,
      recommended: 'home'
    }
  ]);

  // Calculate total stats from 7-day data
  const totalROI = last7DaysROI.reduce((sum, day) => sum + day.roi, 0).toFixed(1);
  const totalWins = last7DaysBets.reduce((sum, day) => sum + day.wins, 0);
  const totalLosses = last7DaysBets.reduce((sum, day) => sum + day.losses, 0);
  const winRate = Math.round((totalWins / (totalWins + totalLosses)) * 100);

  return (
    <DashboardLayout>
      <DashboardContainer>
        <WelcomeSection>
          <Title>Welcome to Odd Genius</Title>
          <Subtitle>Your soccer betting analytics platform</Subtitle>
        </WelcomeSection>
        
        <KPIRow />
        
        <StatsGrid>
          <StatsCard>
            <StatsCardTitle>7-Day ROI Overview (Total: {totalROI > 0 ? `+${totalROI}%` : `${totalROI}%`})</StatsCardTitle>
            <ResponsiveContainer width="100%" height={200}>
              <LineChart data={last7DaysROI} margin={{ top: 5, right: 20, bottom: 5, left: 0 }}>
                <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
                <XAxis dataKey="day" stroke="#8A8AA3" />
                <YAxis stroke="#8A8AA3" />
                <Tooltip 
                  contentStyle={{ backgroundColor: '#252535', borderColor: '#6366F1' }}
                  formatter={(value: number) => [`${value.toFixed(1)}%`, 'ROI']}
                  labelStyle={{ color: '#fff' }}
                />
                <Line 
                  type="monotone" 
                  dataKey="roi" 
                  stroke="#6366F1" 
                  strokeWidth={2}
                  dot={{ stroke: '#6366F1', strokeWidth: 2, r: 4, fill: '#252535' }}
                  activeDot={{ r: 6, stroke: '#6366F1', strokeWidth: 2, fill: '#6366F1' }}
                  isAnimationActive={true}
                />
              </LineChart>
            </ResponsiveContainer>
          </StatsCard>
          
          <StatsCard>
            <StatsCardTitle>7-Day Bet Results (Win Rate: {winRate}%)</StatsCardTitle>
            <ResponsiveContainer width="100%" height={200}>
              <BarChart data={last7DaysBets} margin={{ top: 5, right: 20, bottom: 5, left: 0 }}>
                <CartesianGrid strokeDasharray="3 3" stroke="rgba(255,255,255,0.1)" />
                <XAxis dataKey="day" stroke="#8A8AA3" />
                <YAxis stroke="#8A8AA3" />
                <Tooltip 
                  contentStyle={{ backgroundColor: '#252535', borderColor: '#6366F1' }}
                  labelStyle={{ color: '#fff' }}
                />
                <Legend wrapperStyle={{ color: '#B4B4C6' }} />
                <Bar dataKey="wins" name="Wins" fill="#34D399" radius={[4, 4, 0, 0]} />
                <Bar dataKey="losses" name="Losses" fill="#F87171" radius={[4, 4, 0, 0]} />
              </BarChart>
            </ResponsiveContainer>
          </StatsCard>
        </StatsGrid>
        
        <div>
          <SectionTitle>
            Live Matches
            <ViewAllButton to="/live">
              View all <ArrowRightIcon />
            </ViewAllButton>
          </SectionTitle>
          
          {liveMatches.length > 0 ? (
            <MatchesGrid>
              {liveMatches.map(match => (
                <MatchCard key={match.id}>
                  <LiveBadge>LIVE</LiveBadge>
                  <LeagueHeader>
                    <LeagueName>{match.league}</LeagueName>
                    <MatchTime live={match.isLive}>
                      <LiveIcon /> {match.datetime}
                    </MatchTime>
                  </LeagueHeader>
                  
                  <MatchContent>
                    <TeamsContainer>
                      <TeamInfo>
                        <TeamLogo>{match.homeTeam.substring(0, 1)}</TeamLogo>
                        <TeamName>{match.homeTeam}</TeamName>
                      </TeamInfo>
                      
                      <Score>{match.homeScore} - {match.awayScore}</Score>
                      
                      <TeamInfo>
                        <TeamLogo>{match.awayTeam.substring(0, 1)}</TeamLogo>
                        <TeamName>{match.awayTeam}</TeamName>
                      </TeamInfo>
                    </TeamsContainer>
                    
                    <OddsContainer>
                      <OddButton isHighlighted={match.recommended === 'home'}>
                        {match.homeOdd.toFixed(2)}
                      </OddButton>
                      <OddButton isHighlighted={match.recommended === 'draw'}>
                        {match.drawOdd.toFixed(2)}
                      </OddButton>
                      <OddButton isHighlighted={match.recommended === 'away'}>
                        {match.awayOdd.toFixed(2)}
                      </OddButton>
                    </OddsContainer>
                  </MatchContent>
                </MatchCard>
              ))}
            </MatchesGrid>
          ) : (
            <EmptyState>No live matches at the moment</EmptyState>
          )}
        </div>
        
        <div>
          <SectionTitle>
            Upcoming Matches
            <ViewAllButton to="/upcoming">
              View all <ArrowRightIcon />
            </ViewAllButton>
          </SectionTitle>
          
          {upcomingMatches.length > 0 ? (
            <MatchesGrid>
              {upcomingMatches.map(match => (
                <MatchCard key={match.id}>
                  <LeagueHeader>
                    <LeagueName>{match.league}</LeagueName>
                    <MatchTime>{match.datetime}</MatchTime>
                  </LeagueHeader>
                  
                  <MatchContent>
                    <TeamsContainer>
                      <TeamInfo>
                        <TeamLogo>{match.homeTeam.substring(0, 1)}</TeamLogo>
                        <TeamName>{match.homeTeam}</TeamName>
                      </TeamInfo>
                      
                      <Score>vs</Score>
                      
                      <TeamInfo>
                        <TeamLogo>{match.awayTeam.substring(0, 1)}</TeamLogo>
                        <TeamName>{match.awayTeam}</TeamName>
                      </TeamInfo>
                    </TeamsContainer>
                    
                    <OddsContainer>
                      <OddButton isHighlighted={match.recommended === 'home'}>
                        {match.homeOdd.toFixed(2)}
                      </OddButton>
                      <OddButton isHighlighted={match.recommended === 'draw'}>
                        {match.drawOdd.toFixed(2)}
                      </OddButton>
                      <OddButton isHighlighted={match.recommended === 'away'}>
                        {match.awayOdd.toFixed(2)}
                      </OddButton>
                    </OddsContainer>
                  </MatchContent>
                </MatchCard>
              ))}
            </MatchesGrid>
          ) : (
            <EmptyState>No upcoming matches scheduled</EmptyState>
          )}
        </div>
      </DashboardContainer>
    </DashboardLayout>
  );
};

export default Dashboard;
