import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { Dashboard } from './pages/Dashboard'; // Use named import with curly braces
import { ThemeProvider } from 'styled-components';

// Theme definition
const theme = {
  colors: {
    primary: '#6366F1',
    primaryLight: '#818CF8',
    secondary: '#A855F7',
    success: '#34D399',
    danger: '#F87171',
    warning: '#F59E0B',
    background: '#1a1a2e',
    cardBg: '#252535',
    textPrimary: '#ffffff',
    textSecondary: '#B4B4C6',
    textMuted: '#8A8AA3',
  },
  borderRadius: {
    small: '4px',
    medium: '8px',
    large: '12px',
  },
  shadows: {
    small: '0 2px 5px rgba(0, 0, 0, 0.1)',
    medium: '0 4px 12px rgba(0, 0, 0, 0.1)',
    large: '0 8px 24px rgba(0, 0, 0, 0.15)',
  }
};

// Placeholder components for routes that haven't been implemented yet
const LivePage = () => (
  <div style={{ padding: '50px', color: 'white', textAlign: 'center' }}>
    <h1>Live Matches Page</h1>
    <p>This page is under development.</p>
  </div>
);

const UpcomingPage = () => (
  <div style={{ padding: '50px', color: 'white', textAlign: 'center' }}>
    <h1>Upcoming Matches Page</h1>
    <p>This page is under development.</p>
  </div>
);

const HistoryPage = () => (
  <div style={{ padding: '50px', color: 'white', textAlign: 'center' }}>
    <h1>Match History Page</h1>
    <p>This page is under development.</p>
  </div>
);

const StatisticsPage = () => (
  <div style={{ padding: '50px', color: 'white', textAlign: 'center' }}>
    <h1>Statistics Page</h1>
    <p>This page is under development.</p>
  </div>
);

const SettingsPage = () => (
  <div style={{ padding: '50px', color: 'white', textAlign: 'center' }}>
    <h1>Settings Page</h1>
    <p>This page is under development.</p>
  </div>
);

const App: React.FC = () => {
  return (
    <ThemeProvider theme={theme}>
      <Routes>
        <Route path="/" element={<Dashboard />} />
        <Route path="/live" element={<LivePage />} />
        <Route path="/upcoming" element={<UpcomingPage />} />
        <Route path="/history" element={<HistoryPage />} />
        <Route path="/statistics" element={<StatisticsPage />} />
        <Route path="/settings" element={<SettingsPage />} />
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </ThemeProvider>
  );
};

export default App;
