import React from 'react';
import styled from 'styled-components';
import DashboardLayout from '../components/layout/DashboardLayout';
import KPIRow from '../components/dashboard/KPIRow';

const WelcomeSection = styled.div`
  margin-top: 1.5rem;
`;

const Title = styled.h1`
  font-size: 2rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
`;

const Subtitle = styled.p`
  color: #B4B4C6;
  font-size: 1rem;
`;

const SectionTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: 500;
  margin: 2rem 0 1rem;
`;

const FeaturedMatchesPlaceholder = styled.div`
  color: #B4B4C6;
  padding: 1rem 0;
`;

const Dashboard: React.FC = () => {
  return (
    <DashboardLayout>
      <WelcomeSection>
        <Title>Welcome to Odd Genius</Title>
        <Subtitle>Your soccer betting analytics platform</Subtitle>
      </WelcomeSection>
      
      <KPIRow />
      
      <SectionTitle>Featured Matches</SectionTitle>
      <FeaturedMatchesPlaceholder>
        Match data will appear here in the full version.
      </FeaturedMatchesPlaceholder>
    </DashboardLayout>
  );
};

export default Dashboard;
